// ==UserScript==
// @name         Libert√© d'expression
// @version      1.3
// @description  Encode des messages dans des stickers
// @author       NocturneX
// @match        http://www.jeuxvideo.com/forums/*
// @grant        GM_xmlhttpRequest
// @connect      www.noelshack.com
// ==/UserScript==

(function() {
    'use strict';

	// Obliger de les convertires en base64
	var stickers = [
				"data:image/png;base64,",
		"data:image/png;base64,"
];

    String.prototype.hexEncode = function(){
		var hex, i;

		var result = "";
		for (i=0; i<this.length; i++) {
			hex = this.charCodeAt(i).toString(16);
			result += ("000"+hex).slice(-4);
		}

		return result;
	};

	String.prototype.hexDecode = function(){
		var j;
		var hexes = this.match(/.{1,4}/g) || [];
		var back = "";
		for(j = 0; j<hexes.length; j++) {
			back += String.fromCharCode(parseInt(hexes[j], 16));
		}

		return back;
	};

	function fileToNoelshack(file, callback) {
		var formData = new FormData();
		formData.append('fichier', file);
		GM_xmlhttpRequest({
			method: "POST",
			url: "http://www.noelshack.com/api.php",
			data: formData,
			onload: function(r) {
				callback(r.responseText);
			}
		});
	}

	function dataURLtoFile(dataurl, filename) {
		var arr = dataurl.split(','),
			mime = arr[0].match(/:(.*?);/)[1],
			bstr = atob(arr[1]),
			n = bstr.length,
			u8arr = new Uint8Array(n);
		while (n--) {
			u8arr[n] = bstr.charCodeAt(n);
		}
		return new File([u8arr], filename, {
			type: mime
		});
	}

	function escapeHtml(text) {
  return text
      .replace(/&/g, "&amp;")
      .replace(/</g, "&lt;")
      .replace(/>/g, "&gt;")
      .replace(/"/g, "&quot;")
      .replace(/'/g, "&#039;");
}

	if(document.querySelector("#message_topic") !== null)
	{

		document.querySelectorAll(".img-shack").forEach(function (el) {
			if(/http(s)?:\/\/image.noelshack.com\/(minis|fichiers)\/([0-9]+)\/([0-9]+)\/([0-9]+)\/([0-9]+)-xpr([0-9a-f]{2,})\.png/.test(el.src))
			{
				el.parentNode.innerHTML = '<strong style="color:red">' + escapeHtml(RegExp.$7.hexDecode()) + "</strong> " + el.parentNode.innerHTML;
			}
		});

		let bt = document.createElement("div");
		bt.innerHTML = "<br><a href='#' style='color:#c34e4e' id='liberte'>Cr√©er un message encod√© dans un sticker</a>";
		document.querySelector(".form-post-topic").querySelector(".col-md-12").appendChild(bt);
		document.querySelector("#liberte").onclick = function () {
			let message = prompt("Message √† encoder:");
			while(message.length > 100)
			{
				message = prompt("Erreur ! Le message doit faire moins de 100 caract√®res, message √† encoder:", message);
			}
			bt.querySelector("a").innerHTML = "<br>Chargement...";
			function ci () {
				let img = new Image();   // Cr√©e un nouvel √©l√©ment Image
				img.src = stickers[Math.floor(Math.random()*stickers.length)];
				img.crossorigin = "anonymous";
				img.onload = function() {
					let canv = document.createElement('canvas');
					canv.width = img.width;
					canv.height = img.height;
					let context = canv.getContext("2d");
					context.fillStyle = "white";
					context.fillRect(0, 0, img.width, img.height);
					context.drawImage(img, 0, 0);
					let file = dataURLtoFile(canv.toDataURL("image/png"), "xpr" + message.hexEncode() + ".png");
					fileToNoelshack(file, function (lien) {
						if(/noelshack/.test(lien))
						{
							document.querySelector("#message_topic").value += " " + lien;
						}
						else
						{
							alert("Une erreur est survenue !");
						}
						bt.querySelector("a").innerHTML = "Cr√©er un message encod√© dans un sticker";
					});
				};
				img.onerror = function () {
					console.log("erreur on recommence")
					ci ();
				};
			}
			ci();
			return false;
		};
	}
})();
